class ArrayableArray extends ArrayList<Object> { 
 public Class type; 
 public ArrayableArray parent; 
 public ArrayableArray(Class type) { 
 this.type = type; 
 } 
 public ArrayableArray() { 
 this.type = Integer.class; 
 } 
 @Override 
 public boolean add(Object obj) { 
 if(obj.getClass() == this.type) { 
 super.add(obj); 
 } else { 
 if(obj instanceof ArrayableArray) { 
 ArrayableArray arr = (ArrayableArray)obj; 
 if(arr.type == this.type) { 
 super.add(arr); 
 arr.parent = this; 
 } else { 
 throw new IllegalArgumentException("Given array type does not match the parent Array type. Given type:"".concat(arr.type.toString()).concat("" Array type:"").concat(this.type.toString()).concat(""")); 
 } 
 } else { 
 throw new IllegalArgumentException("Given value type does not match the Array value type. Given type:"".concat(obj.getClass().toString()).concat("" Array type:"").concat(this.type.toString()).concat(""")); 
 } 
 } 
 return true; 
 } 
 @Override 
 public void add(int index,Object obj){ 
 if(obj.getClass() == this.type) { 
 super.add(index,obj); 
 } else { 
 if(obj instanceof ArrayableArray) { 
 ArrayableArray arr = (ArrayableArray)obj; 
 if(arr.type == this.type) { 
 super.add(index,arr); 
 arr.parent = this; 
 } else { 
 throw new IllegalArgumentException("Given array type does not match the parent Array type. Given type:"".concat(arr.type.toString()).concat("" Array type:"").concat(this.type.toString()).concat(""")); 
 } 
 } else { 
 throw new IllegalArgumentException("Given value type does not match the Array value type. Given type:"".concat(obj.getClass().toString()).concat("" Array type:"").concat(this.type.toString()).concat(""")); 
 } 
 } 
 } 
