public void Notch(final int width,final int height,final int rdIn,final int rdOut,final int offset,final String color,final View view) {
view.setDrawingCacheEnabled(true);
Bitmap bitmap = Bitmap.createBitmap(view.getDrawingCache());
bitmap = loadBitmapFromView(view,view.getWidth(),view.getHeight());
view.setDrawingCacheEnabled(false);
Canvas canvas = new Canvas(bitmap);
drawCorner(new Rect(-rdOut,-rdOut,rdOut,rdOut),canvas,Color.parseColor(color),270F,canvas.getWidth()/2-width/2-rdOut,0+offset);
drawMiddle(new Rect(0,0,width,height),canvas,Color.parseColor(color),rdIn,canvas.getWidth()/2-width/2,0+offset);
drawCorner(new Rect(-rdOut,-rdOut,rdOut,rdOut),canvas,Color.parseColor(color),180F,canvas.getWidth()/2+width/2,0+offset);
android.graphics.drawable.BitmapDrawable bkg = new android.graphics.drawable.BitmapDrawable(bitmap);
view.setBackground(bkg);
}
public static Bitmap loadBitmapFromView(View v, int width, int height) {
Bitmap b = Bitmap.createBitmap(width , height, Bitmap.Config.ARGB_8888);
Canvas c = new Canvas(b);
v.layout(0, 0, width, height);
android.graphics.drawable.Drawable bgDrawable =v.getBackground();
if (bgDrawable!=null)
bgDrawable.draw(c);
else
c.drawColor(Color.WHITE);
v.draw(c);
return b;
}
public static Bitmap rotateBitmap(Bitmap source, float angle) {
Matrix matrix = new Matrix(); matrix.postRotate(angle);
return Bitmap.createBitmap(source, 0, 0, source.getWidth(), source.getHeight(), matrix, true);
}
public void drawMiddle(final Rect rect,final Canvas canvas,final int color,final float round,final float x,final float y) {
Bitmap bitmap = Bitmap.createBitmap(rect.width(),rect.height(),Bitmap.Config.ARGB_8888);
Paint paint = new Paint(Paint.ANTI_ALIAS_FLAG);
paint.setColor(color);
paint.setStyle(Paint.Style.FILL);
Canvas canvas1 = new Canvas(bitmap);
Path path = new Path();
path.addRoundRect(new RectF(rect),new float[]{0F,0F,0F,0F,round,round,round,round},Path.Direction.CW);
canvas1.drawColor(Color.TRANSPARENT);
canvas1.drawPath(path,paint);
canvas.drawBitmap(bitmap,x,y,null);
}
public void drawCorner(final Rect rect,final Canvas canvas,final int color,final float rot,final float x,final float y) {
Bitmap bitmap = Bitmap.createBitmap(rect.width()/2,rect.height()/2,Bitmap.Config.ARGB_8888);
Canvas canvas1 = new Canvas(bitmap);
Paint paint = new Paint(Paint.ANTI_ALIAS_FLAG);
paint.setColor(color);
paint.setStyle(Paint.Style.FILL);
paint.setXfermode(new PorterDuffXfermode(PorterDuff.Mode.CLEAR));
canvas1.drawColor(color);
canvas1.drawArc(new RectF(rect),0F,90F,true,paint);
canvas.drawBitmap(rotateBitmap(bitmap,rot),x,y,null);

