public interface DragListener {
public void onDrag(View v,DraggableDialog dialog);
public void onDragStart(View v,DraggableDialog dialog);
public void onDragEnd(View v,DraggableDialog dialog);
}
public class DraggableDialog extends FrameLayout {
private View root;
private DraggableDialog dialog;
public DraggableDialog(Context context) {
super(context);
root = this;
dialog = this;
LinearLayout.LayoutParams par = new LinearLayout.LayoutParams(LinearLayout.LayoutParams.WRAP_CONTENT,LinearLayout.LayoutParams.WRAP_CONTENT);
root.setLayoutParams(par);
}
public DraggableDialog(Context context,int width,int height) {
super(context);
root = this;
dialog = this;
LinearLayout.LayoutParams par = new LinearLayout.LayoutParams(width,height);
root.setLayoutParams(par);
}
void addDragListener(final DragListener listener,final View view) {
view.setOnTouchListener(new OnTouchListener() {
PointF DownPT = new PointF();
PointF StartPT = new PointF();
@Override
public boolean onTouch(View v, MotionEvent event) {
int eid = event.getAction();
switch (eid) {
case MotionEvent.ACTION_MOVE:
listener.onDrag(view,dialog);
PointF mv = new PointF(event.getX() - DownPT.x, event.getY() - DownPT.y);
Rect rect = new Rect();
root.getParent().getLocalVisibleRect(rect);
if((StartPT.x+mv.x>=rect.left)&&(StartPT.x+mv.x<=rect.right-(root.getWidth()))) {
root.setX((int)(StartPT.x+mv.x));
} else {
if(!(StartPT.x+mv.x>=rect.left)) {
root.setX(root.getParent().getX());
}
if(!(StartPT.x+mv.x<=rect.right-root.getWidth())) {
root.setX(root.getParent().getX()+root.getParent().getWidth()-root.getWidth());
}
}
if((StartPT.y+mv.y>=rect.top)&&(StartPT.y+mv.y<=rect.bottom-(root.getHeight()))) {
root.setY((int)(StartPT.y+mv.y));
} else {
if(!(StartPT.y+mv.y>=rect.top)) {
root.setY(root.getParent().getY());
}
if(!(StartPT.y+mv.y<=rect.bottom-root.getHeight())) {
root.setY(root.getParent().getY()+root.getParent().getHeight()-root.getHeight());
}
}
StartPT = new PointF(root.getX(), root.getY());
break;
case MotionEvent.ACTION_DOWN :
listener.onDragStart(view,dialog);
DownPT.x = event.getX();
DownPT.y = event.getY();
StartPT = new PointF(root.getX(), root.getY());
break;
case MotionEvent.ACTION_UP :
listener.onDragEnd(view,dialog);
break;
default : break;
}
return true;
}
});
}
