	ArrayList<View>_v = new ArrayList<>();
	_v.add(view);
	if(!(view instanceof ViewGroup)) return _v;
	for(int i = 0; i < ((ViewGroup)view).getChildCount();i++){
		final View child = ((ViewGroup)view).getChildAt(i);
		if (child instanceof ViewGroup) {
			_v.addAll(getAllChildren(child));
		}
		else {
			_v.add(child);
		}
	}
	return _v;
}

