public class BubbleLinearLayout extends LinearLayout {

public class BubbleChild { private int base, size, speed, a, rot; private double x, y, z, range; }

private Paint da = new Paint();
private ArrayList<BubbleChild> dg = new ArrayList<>();
private int padding = 0;

public BubbleLinearLayout(Context context, int pd, int color) {
	super(context);
	setOrientation(LinearLayout.VERTICAL);
	da.setColor(color);
	padding = pd;
	setBackgroundColor(Color.TRANSPARENT);
}

protected void onDraw(Canvas canvas) {
	for(int i=0;i<dg.size();i++) {
		da.setAlpha(dg.get(i).a);
		canvas.drawCircle((float)dg.get(i).x, (float)dg.get(i).y, dg.get(i).size, da);
	};
}

private void addBubble() {
	BubbleChild data = new BubbleChild();
	data.x = getRandom(padding, getWidth()-padding);
	data.y = getRandom(padding, getHeight()-padding);
	data.z = 0;
	data.a = 0;
	data.rot = getRandom(0,360);
	data.speed = getRandom(4,10);
	data.size = getRandom((int)getDip(3), (int)getDip(9));
	dg.add(data);
	invalidate();
}

private void removeBubble(int _pos) {
	dg.remove(_pos);
}

private int getBubbles() {
	return dg.size();
}

private BubbleChild getBubbleAt(int _pos) {
	return dg.get(_pos);
}

private int getRandom(int a1, int a2) {
	Random rand = new Random();
	return rand.nextInt(a2 - a1 + 1) + a1;
}

private int getDip(int val) {
	return (int)TypedValue.applyDimension(TypedValue.COMPLEX_UNIT_DIP, val, getResources().getDisplayMetrics());
}

